import React from "react";
import { useFormik } from "formik";
import * as Yup from "yup";

export default function NoteForm(props) {
  const formik = useFormik({
    initialValues: {
      title: "",
      description: "",
      category: "",
    },
    validationSchema: Yup.object().shape({
      title: Yup.string().max(20, "Error").required("Title is required"),
    }),
    onSubmit: () => props.handleSubmittedNote(formik.values),
  });
  return (
    <form onSubmit={formik.handleSubmit}>
      <input
        name="title"
        type="text"
        placeholder="Title"
        value={formik.values.title}
        onChange={formik.handleChange}
      />
      {formik.errors.title ? <div>{formik.errors.title}</div> : "No Error"}
      <select
        name="category"
        placeholder="Category"
        onChange={formik.handleChange}
        value={formik.values.category}
      >
        <option value="statistics">Statistics</option>
        <option value="data_mining">Data Mining</option>
        <option value="deep_learning">Deep Learning</option>
      </select>
      <input
        name="description"
        type="text"
        placeholder="Description"
        onChange={formik.handleChange}
        value={formik.values.description}
      />
      <button type="submit" onClick={formik.handleSubmit}>
        Submit
      </button>
      <button type="button" onClick={formik.resetForm}>
        Reset
      </button>
      <button type="button" onClick={props.handleCancel}>
        Cancel
      </button>
    </form>
  );
}
