import React, { useState, useEffect } from "react";
import "./App.css";
import NoteForm from "./components/NoteForm";
import * as uuid from "uuid";
import { Container, Row, Col, Button } from "react-bootstrap";
import SingleNote from "./components/SingleNote";

function App() {
  // State Hooks
  const [notes, setNotes] = useState([]);
  const [editing, setEditing] = useState(false);
  const [show, setShow] = useState(false);

  const [isClicked, setIsClicked] = useState(false);

  useEffect(() => {
    console.log("tis is show", show);
    return () => {
      console.log("stop show");
    };
  }, [show]);

  // Event Handlers
  const handleClose = () => {
    setShow(false);
    setEditing(false);
  };

  const handleShow = () => {
    setShow(true);
    setIsClicked(false);
  };
  const handleEdit = () => setEditing(true);

  function handleAdd() {
    setIsClicked(true);
  }

  function handleCancel() {
    setIsClicked(false);
  }
  function handleSubmittedNote(note) {
    note.id = uuid.v4();
    setShow(false);
    setNotes([...notes, note]);
  }

  function handleEditCancel() {
    setEditing(false);
  }

  function handleDelete(note) {
    const newNotes = notes.filter((noteIndex) => noteIndex.id !== note.id);
    setShow(false);
    setNotes(newNotes);
  }

  function handleSubmitChanges(note) {
    let newEdittedNotes = [...notes];
    const index = newEdittedNotes.findIndex(
      (oldNote) => oldNote.id === note.id
    );
    newEdittedNotes[index] = note;
    setNotes(newEdittedNotes);

    setEditing(false);
    test();
  }

  function test() {
    console.log("htest");
  }

  return (
    <Container className="App">
      <Row>
        <Col>
          {notes.map((note) => (
            <SingleNote
              key={note.id}
              note={note}
              editing={editing}
              show={show}
              handleShow={handleShow}
              handleClose={handleClose}
              handleEdit={handleEdit}
              initialValues={{
                title: note.title,
                description: note.description,
                category: note.category,
                id: note.id,
              }}
              handleEditCancel={handleEditCancel}
              handleDelete={handleDelete}
              handleSubmitChanges={handleSubmitChanges}
            />
          ))}
        </Col>
        <Col className="column">
          <div className="logo">Notes App</div>
          <div>
            <Button className="add-button" onClick={handleAdd}>
              Add new Note
            </Button>
          </div>
          {isClicked ? (
            <NoteForm
              handleSubmittedNote={handleSubmittedNote}
              handleCancel={handleCancel}
              disabled={editing}
              initialValues={{
                title: "",
                description: "",
                category: "General",
              }}
            />
          ) : (
            <div></div>
          )}
        </Col>
      </Row>
    </Container>
  );
}

export default App;
